Module.Content:

#requireResource("com.atlassian.gadgets.publisher:ajs-gadgets")
#requireResource("com.pawelniewiadomski.jira.confluence-openid-authentication-plugin:openid-login-gadget-resources")
#includeResources()

<script type="text/javascript">
    AJS.Gadget({
        baseUrl: "__ATLASSIAN_BASE_URL__",
        view: {
            enableReload: false,

            template: function () {
                var gadget = this;

                AJS.$.ajax({
                    type: "GET",
                    url: "__ATLASSIAN_BASE_URL__/rest/jira-openid-authentication/1.0/openIdProviders/login",
                    success: function(data) {
                        if (AJS.$.isArray(data) && data.length > 0) {
                            gadget.getView().addClass("g-openid").html(OpenId.Templates.Gadgets.login({
                                baseUrl: "__ATLASSIAN_BASE_URL__",
                                providers: data
                            }));
                        }
                    }
                });
            }
        }
    });
</script>
